# Get list of all subpages
subpages = [website_url + anchor.get('href', None) for anchor in anchors if "/dataset/nyc-taxi-zones/resource" in anchor.get('href', None) and "?inner_span=True" in anchor.get('href', None)]

# Get list of all downloads
anchors_download = []
for subpage in subpages:
    subpage_html = urllib.request.urlopen(subpage).read()
    subpage_soup = BeautifulSoup(subpage_html, 'html.parser')
    subpage_anchors = subpage_soup.find_all('a', href=True, id="res_url")

    for download_URL in subpage_anchors:
        if "data.cityofnewyork.us/api/geospatial" in download_URL.get('href', None):
            anchors_download.append(download_URL.get('href', None))
        elif "data.cityofnewyork.us/api/views/" in download_URL.get('href', None) and "accessType=DOWNLOAD" in download_URL.get('href', None):
            anchors_download.append(download_URL.get('href', None))

# Define functions to determine filename and filepath
def getFilename(url):
    return os.path.basename(url).split('?')[0]

def getFilepath(url):
    if "format=KML" in url:
        result = '/raw/taxizone_Maps.kml'
    elif "format=KMZ" in url:
        result = '/raw/taxizone_Maps.kmz'
    elif "format=Shapefile" in url:
        result = '/raw/taxizone_Maps.Shapefile'
    elif "format=Original" in url:
        result = '/raw/taxizone_Maps.original'
    elif "rows.json" in url:
        result = '/raw/taxizone_Maps.json'
    elif "rows.csv" in url:
        result = '/raw/taxizone_Maps.csv'
    else:
        result = '/raw/misc'
    return(result)

# Build comma separated list of all downloads
anchors_fqdn = []
for anchor in anchors_download:
    if anchor[0] is '/':
        anchors_fqdn.append(website_url + anchor + "," + getFilename(website_url + anchor) + "," + getFilepath(website_url + anchor))
    else:
        anchors_fqdn.append(anchor + "," + getFilename(anchor) + "," + getFilepath(anchor))

# One URL not listed on the pages
anchors_fqdn.append('https://catalog.data.gov/harvest/object/c03a3d91-4a20-4625-b3b7-839544c490a8,metadata.json,/raw/taxizone_Metadata.json')
